export interface astronomy {
    sunrise: string, sunset: string
}

export interface atmosphere {
    humidity: number, visibility: number, pressure: number, rising: number
}

export interface condition {
    text: string, code: number, temperature: number
}

export interface wind {
    chill: number, direction: number, speed: number
}

export interface currentObservition {
    astronomy: astronomy;
    atmosphere: atmosphere;
    condition: condition;
    pubDate: string;
    wind: wind; 
}

export interface forecasts {
    code: number;
    date: string;
    day: string;
    high: number;
    low: number;
    text: string;
}

export interface location {
    city: string;
    country: string;
    lat: string;
    long: string;
    region: string;
    timezone_id: string;
    woeid: string;
}

export interface weatherApi {
    current_observation: any;
    forecasts: any;
    location: any;
}

export class weatherJson implements weatherApi {
    current_observation: any;
    forecasts: any;
    location: any;

    constructor(current_observation: any, forecasts: any, location:any)
    {
        this.current_observation = current_observation;
        this.forecasts = forecasts;
        this.location = location
    }
}

export class WeatherData {
    private data: weatherJson;
    
    constructor(data: weatherApi)
    {
        this.data = data;
    }

    location() 
    {
        return this.data['location'];
    }

    current_observation()
    {
        return this.data['current_observation'];
    }

    forecasts()
    {
        return this.data['forecasts'];
    }
}

export class WeekDays {
    protected weekday: object = [
        {'long': 'Pazar', 'short': 'Pzr'},
        {'long': 'Pazartesi', 'short': 'Pzrts'},
        {'long': 'Salı', 'short': 'Sal'},
        {'long': 'Çarşamba', 'short': 'Çrşm'},
        {'long': 'Perşembe', 'short': 'Per'},
        {'long': 'Cuma', 'short': 'Cum'},
        {'long': 'Cumartesi', 'short': 'Cmrts'}
    ];

    getDay(day: any) 
    {
        return this.weekday[day];
    }
}

